(function($){$.fn.bxTabs=function(options){var settings=$.extend({},$.fn.bxTabs.defaults,options);var menuTabs=this.each(function(index){$(this).click(function(){$.fn.bxTabs.goToTab(index);});});$.fn.bxTabs.goToTab=function(tabIndex){var o=$.meta?$.extend({},settings,$this.data()):settings;menuTabs.filter('.selected').removeClass('selected');menuTabs.eq(tabIndex).addClass('selected');$(o.tabbedContentSelector+'.current').removeClass('current');$(o.tabbedContentSelector+':eq('+tabIndex+')').addClass('current');return false;};}
$.fn.bxTabs.defaults={tabbedContentSelector:'.bxTabContent li'};})(jQuery);